// === FINAL BULLETPROOF RERANKER â€“ works no matter how n8n passes the data ===

// Find the array that contains the results (covers all common cases)
let results = [];

// Case 1: input is [{ results: [...] }]
if ($input.all()[0]?.json?.results) {
  results = $input.all()[0].json.results;
}
// Case 2: input is [[{...}, {...}]] â€“ already flattened array of chunks
else if (Array.isArray($input.all()[0]?.json)) {
  results = $input.all().map(item => item.json).flat();
}
// Case 3: input is direct array of chunks
else if (Array.isArray($input.first()?.json)) {
  results = $input.first().json;
}
// Fallback: grab everything that looks like a result
else {
  results = $input.all().map(i => i.json).flat().filter(Boolean);
}

if (results.length === 0) {
  return [{ json: { error: "No results found in input" } }];
}

// 1. Deduplicate: one best chunk per poem_id
const bestPerPoem = {};
for (const r of results) {
  const id = r.metadata?.poem_id;
  if (!id) continue;
  if (!bestPerPoem[id] || r.scores.final > bestPerPoem[id].scores.final) {
    bestPerPoem[id] = r;
  }
}

const poems = Object.values(bestPerPoem);

// 2. Sentiment-first reranking (your projectâ€™s real priority)
poems.forEach(p => {
  const s = p.scores || {};
  let score = 0;

  score += (s.sentiment || 0) * 6.0;   // sentiment dominates
  score += (s.vector || 0) * 6.0;      // meaning still strong
  score += (s.entity || 0) * 2.7;
  score += (s.trigram || 0) * 1.5;
  score += (s.final || 0) * 0.5;

  p.rerank_score = Number(score.toFixed(4));
});

// 3. Return poems with rerank_score >= 60
const topPoems = poems
  .filter(p => p.rerank_score >= 19)  // only keep high-score poems
  .sort((a, b) => b.rerank_score - a.rerank_score);

// Return for n8n
return topPoems.map(p => ({ json: p }));

// Extract and parse the JSON, return as nested object in "query" field
const firstItem = $input.first();
const content = firstItem.json.message?.content || '';

let parsedData;

if (!content) {
  parsedData = {
    original_query: "",
    expanded_entities: "",
    sentiments: "Neutral",
    match_count: 10
  };
} else {
  const jsonMatch = content.match(/```json\n([\s\S]*?)\n```/);

  if (jsonMatch) {
    parsedData = JSON.parse(jsonMatch[1]);
  } else {
    try {
      parsedData = JSON.parse(content);
    } catch (error) {
      parsedData = {
        original_query: "",
        expanded_entities: "",
        sentiments: "Neutral",
        match_count: 10
      };
    }
  }
}

return [{
  json: {
    query: parsedData
  }
}];
