parameters.jsCode:
const items = $input.all();
let results = [];

items.forEach(item => {
  let outputStr = item.json.output || "";

  // Remove markdown code blocks and any leading/trailing whitespace
  outputStr = outputStr.replace(/```json|```/g, '').trim();

  // Remove all whitespace characters that could interfere (newlines, tabs, etc.)
  // but keep spaces inside strings
  const normalized = outputStr.replace(/\s+/g, ' ');

  // Regex to match objects: {"Row_ID": "number", "event": ...}
  // Captures Row_ID and the full event part (array or string)
  const objectRegex = /{\s*"Row_ID"\s*:\s*"([^"]+)"\s*,\s*"event"\s*:\s*(\[[^\]]*\]|"[^"]*")\s*}/g;

  let match;
  while ((match = objectRegex.exec(normalized)) !== null) {
    const rowId = match[1].trim();
    const eventRaw = match[2].trim();

    let eventValue;

    if (eventRaw === '[]') {
      eventValue = [];
    } else if (eventRaw.startsWith('[') && eventRaw.endsWith(']')) {
      // Handle multiple tags: ["رمضان", "ليلة القدر"]
      try {
        eventValue = JSON.parse(eventRaw);
      } catch (e) {
        eventValue = [];
      }
    } else {
      // Single quoted string
      eventValue = eventRaw.slice(1, -1); // remove quotes
    }

    results.push({
      Row_ID: rowId,
      event: eventValue
    });
  }
});

// Return as proper n8n items
return results.map(result => ({ json: result }));

const items = $input.all();
const grouped = items.reduce((acc, item) => {
  const json = item.json;
  // Create unique key combining poem_id and title to distinguish different poem instances
  const uniqueKey = `${json.poem_id}-${json.Title_cleaned}`;
  if (!acc[uniqueKey]) {
    acc[uniqueKey] = {
      poem_id: json.poem_id,
      poem: json.Title_cleaned.trim(),
      contents_tagged: []
    };
  }
  const lineText = (json.Poem_line_cleaned || "").trim();
  const rowId = json.Row_ID;  // This line is present in your original code
  acc[uniqueKey].contents_tagged.push(`"${rowId}":"${lineText}"`);
  return acc;
}, {});
const result = Object.values(grouped);
return result;

