const items = $input.all();
if (!Array.isArray(items) || items.length === 0) {
  return [{ json: { error: "No input items" } }];
}

const result = items.flatMap(item => {
  const outputStr = item.json?.output;
  if (typeof outputStr !== "string" || !outputStr.trim()) {
    return [{
      json: {
        Row_ID: item.json?.Row_ID ?? "unknown",
        summary: {
          error: "empty_output",
          message: "No output string found in this item."
        }
      }
    }];
  }

  // Clean common LLM wrappers
  const cleanJsonStr = outputStr.replace(/```json|```/g, "").trim();

  let parsed;
  try {
    parsed = JSON.parse(cleanJsonStr);
  } catch (e) {
    return [{
      json: {
        Row_ID: item.json?.Row_ID ?? "unknown",
        summary: {
          error: "json_parse_failed",
          message: e.message
        }
      }
    }];
  }

  if (!Array.isArray(parsed)) {
    return [{
      json: {
        Row_ID: item.json?.Row_ID ?? "unknown",
        summary: {
          error: "invalid_format",
          message: "Parsed output is not an array."
        }
      }
    }];
  }

  return parsed.map(entry => {
    if (!entry.line || typeof entry.line !== "string" || !entry.line.trim()) {
      return {
        json: {
          Row_ID: String(entry.row_id ?? "unknown"),
          summary: {
            error: "invalid_input",
            message: "Expected raw Arabic poetry line in field 'line' or 'Poem_line'.",
            original: entry
          }
        }
      };
    }

    // Valid entry
    return {
      json: {
        Row_ID: String(entry.row_id ?? ""),
        summary: { ...entry }
      }
    };
  });
});

return result;

