      poem: json.Title_cleaned.trim(),
      contents_tagged: []
    };
  }

  const lineText = (json.Poem_line_cleaned || "").trim();
  const rowId = json.Row_ID;

  acc[uniqueKey].contents_tagged.push(`"${rowId}":"${lineText}"`);

  return acc;
}, {});

