parameters.jsCode:
for (const item of $input.all()) {
const rawOutput = item.json?.output || "";
const cleaned = rawOutput.replace(/`/g, "").trim();
const tashkeelMatch = cleaned.match(
/tashkeel\s*:\s*([\s\S]*?)(?=,\s*sentiment\s*:|$)/i
);
const sentimentMatch = cleaned.match(
/sentiment\s*:\s*([^\s,]+)/i
);
item.json.tashkeel = tashkeelMatch ? tashkeelMatch[1].trim() : null;
item.json.sentiment = sentimentMatch ? sentimentMatch[1].trim() : null;
// 🔥 IMPORTANT: remove heavy field
delete item.json.output;
}
return $input.all();
