// Paste into an n8n Function node

// Config: tune this threshold as you like
const MIN_SCORE = 0.19; // keep low like 0.19, or raise to 1.0/2.0/5.0 for stricter filtering

// 0) Collect/normalize incoming results (cover common shapes)
let results = [];

const firstJson = $input.first()?.json;
if (firstJson?.results && Array.isArray(firstJson.results)) {
  results = firstJson.results;
} else if (Array.isArray(firstJson) && firstJson.length && typeof firstJson[0] === 'object') {
  // firstJson itself is an array of items
  results = firstJson;
} else {
  // try a variety of flattening approaches
  results = $input.all().map(i => {
    const j = i.json;
    if (!j) return null;
    if (Array.isArray(j.results)) return j.results;
    if (Array.isArray(j)) return j;
    // sometimes node outputs single objects representing a result
    return j;
  }).flat().filter(Boolean);
}

// Safety: if still empty, return an error object so you can debug
if (!results || results.length === 0) {
  return [{ json: { error: "No results found in input (checked common shapes).", raw_input: $input.all().map(i=>i.json) } }];
}

// 1) Deduplicate: prefer one best chunk per poem_id (try multiple poem-id field names)
const bestPerPoem = {};
for (const r of results) {
  // try several possible keys where a poem_id might live
  const poemId = r.poem_id ?? r.poemId ?? r.metadata?.poem_id ?? r.metadata?.poemId ?? r.metadata?.poemIdStr ?? null;
  if (!poemId) continue;

  const rFinal = Number(r.scores?.final ?? r.final ?? 0);
  if (!bestPerPoem[poemId] || rFinal > Number(bestPerPoem[poemId].scores?.final ?? bestPerPoem[poemId].final ?? 0)) {
    bestPerPoem[poemId] = r;
  }
}

// If we found no poem_id keys, fall back to using the full results array (no dedupe)
let poems = Object.values(bestPerPoem);
if (poems.length === 0) {
  poems = results.slice(); // copy
}

// 2) Compute rerank_score (sentiment-first formula from your original)
poems.forEach(p => {
  const s = p.scores ?? {};
  const vector = Number(s.vector ?? p.vector ?? 0);
  const sentiment = Number(s.sentiment ?? p.sentiment ?? 0);
  const entity = Number(s.entity ?? p.entity ?? 0);
  const trigram = Number(s.trigram ?? p.trigram ?? 0);
  const final = Number(s.final ?? p.final ?? 0);

  let score = 0;
  score += sentiment * 6.0;
  score += vector * 6.0;
  score += entity * 2.7;
  score += trigram * 1.5;
  score += final * 0.5;

  p.rerank_score = Number(score.toFixed(6));
});

// 3) Filter and sort
const topPoems = poems
  .filter(p => Number.isFinite(p.rerank_score) && p.rerank_score >= MIN_SCORE)
  .sort((a, b) => b.rerank_score - a.rerank_score);

// 4) Return items in n8n format (one item per output)
return topPoems.map(p => ({ json: p }));

// Extract and parse the JSON, return as nested object in "query" field
const firstItem = $input.first();
const content = firstItem.json.message?.content || '';

let parsedData;

if (!content) {
  parsedData = {
    original_query: "",
    expanded_entities: "",
    sentiments: "Neutral",
    match_count: 10
  };
} else {
  const jsonMatch = content.match(/```json\n([\s\S]*?)\n```/);

  if (jsonMatch) {
    parsedData = JSON.parse(jsonMatch[1]);
  } else {
    try {
      parsedData = JSON.parse(content);
    } catch (error) {
      parsedData = {
        original_query: "",
        expanded_entities: "",
        sentiments: "Neutral",
        match_count: 10
      };
    }
  }
}

return [{
  json: {
    query: parsedData
  }
}];

